mapper.sql:
	FROM
		codegroup a

	WHERE 1=1



                    AND(
						a.cgSeq Like CONCAT('%',#{shValue},'%')
						OR a.cgName Like CONCAT('%',#{shValue},'%')
					)


				    AND a.cgSeq = #{shValue}


				    AND a.cgName LIKE CONCAT('%', #{shValue}, '%')




			AND a.cgUseNy = #{shUseNy}


			AND a.cgDelNy = #{shDelNy}



				 AND a.cgRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
				 AND a.cgModDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
    	UPDATE codegroup 
		set
			cgName = #{cgName}
			,cgUseNy = #{cgUseNy}
			,cgOrder = #{cgOrder}
			,cgDesc = #{cgDesc}
			,cgModDate = now()
		WHERE 1=1
			AND cgSeq = #{cgSeq}

mapper.delete:
DELETE FROM codegroup
where 1=1
	and cgSeq = #{cgSeq}
