mapper.sql:
FROM
	commoncode a
	left join codegroup b on a.codegroup_cgSeq = b.cgSeq
where 1=1
	AND a.delNy = #{sDelNy}
	AND a.useNy = #{sUseNy}



				AND(
					a.cdSeq Like CONCAT('%',#{shValue},'%')
					OR b.cgName Like CONCAT('%',#{shValue},'%')
					OR b.cgSeq Like CONCAT('%',#{shValue},'%')
					OR a.cdName Like CONCAT('%',#{shValue},'%')
				)

			AND a.cdSeq = #{shValue}
			AND a.cdName Like CONCAT('%',#{shValue},'%') 
			AND b.cgSeq Like CONCAT('%',#{shValue},'%')
			AND b.cgName Like CONCAT('%',#{shValue},'%')



		 AND a.cdRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
		 AND a.cdModDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
UPDATE commoncode 
set
	cdName = #{cdName}
	,cdUseNy = #{cdUseNy}
	,cdOrder = #{cdOrder}
	,cdDesc = #{cdDesc}
	,cdModDate = now()
	,codegroup_cgSeq = #{codegroup_cgSeq}
WHERE 1=1
	AND cdSeq = #{cdSeq}

mapper.delete:
DELETE FROM commoncode
where 1=1
	and cdSeq = #{cdSeq}
