mapper.sql:
FROM mappoint a
	left join fishandmappoint b on b.mappoint_mpSeq = a.mpSeq
    left join fish c on c.fsSeq = b.fish_fsSeq
WHERE 1=1



            AND(
                a.mpTitle LIKE CONCAT('%', #{shValue}, '%')
                OR a.mpText LIKE CONCAT('%', #{shValue}, '%')
            )


            AND a.mpTitle = #{shValue}


            AND a.mpAddress LIKE CONCAT('%', #{shValue}, '%')




    AND a.mpDelNy = #{shDelNy}


    AND a.mpType = #{shType}



		 AND a.mpRegDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
    	INSERT INTO mappoint(
    		mpTitle
    		,mpText
    		,mpAddress
    		,mpType
    		,mpLatitude
    		,mpLongitude
    		,mpDelNy
    		,mpRegDate
    	)VALUES(
    		#{mpTitle}
    		,#{mpText}
    		,#{mpAddress}
    		,#{mpType}
    		,#{mpLatitude}
    		,#{mpLongitude}
    		,0
    		,now()
    	)

			SELECT last_insert_id()

mapper.update:
    	UPDATE mappoint
    	SET
    		mpTitle = #{mpTitle}
    		,mpText = #{mpText}
    		,mpAddress = #{mpAddress}
    		,mpLatitude = #{mpLatitude}
    		,mpLongitude = #{mpLongitude}
    		,mpType = #{mpType}
		WHERE 1=1
		AND mpSeq = #{mpSeq}

