mapper.sql:
FROM orders g LEFT JOIN ship a ON g.ship_spSeq = a.spSeq WHERE 1=1
AND( a.spName LIKE CONCAT('%', #{shValue}, '%') OR g.odTotalPrice LIKE CONCAT('%', #{shValue}, '%') )
AND a.spName = #{shValue}
AND g.odTotalPrice LIKE CONCAT('%', #{shValue}, '%')
AND g.odRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
