mapper.sql:
FROM port a
	left join imginsert_port b on b.pseq = a.ptSeq and b.defaultNy = 1
WHERE 1=1



            AND(
                a.ptName LIKE CONCAT('%', #{shValue}, '%')
                OR a.ptAddress LIKE CONCAT('%', #{shValue}, '%')
            )


            AND a.ptName = #{shValue}


            AND a.ptAddress LIKE CONCAT('%', #{shValue}, '%')




    AND a.ptDelNy = #{shDelNy}


    AND a.ptArea = #{shPtArea}


	AND a.ptName LIKE CONCAT('%', #{shPtName}, '%')



		 AND a.ptRegDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
UPDATE port
SET
	ptArea = #{ptArea}
	,ptName = #{ptName}
	,ptDesc = #{ptDesc}
	,ptAddress = #{ptAddress}
WHERE 1=1
AND ptSeq = #{ptSeq}

mapper.insert:
insert into imginsert_port(
type
,defaultNy
,sort
,path
,originalName
,uuid
,ext
,size
,regDate
,modDate
,delNy
,pseq
) value(
#{type}
,#{defaultNy}
,#{sort}
,#{path}
,#{originalName}
,#{uuid}
,#{ext}
,#{size}
,now()
,now()
,0
,#{pseq}
)

	SELECT last_insert_id()
