mapper.sql:
FROM ship a

WHERE 1=1



            AND(
                a.spName LIKE CONCAT('%', #{shValue}, '%')
                OR a.spPrice LIKE CONCAT('%', #{shValue}, '%')
                OR a.spTotal LIKE CONCAT('%', #{shValue}, '%')
            )


            AND a.spName = #{shValue}


            AND a.spPrice LIKE CONCAT('%', #{shValue}, '%')


            AND a.Total LIKE CONCAT('%', #{shValue}, '%')




    AND a.spDelNy = #{shDelNy}


    AND a.spArea = #{shArea}


    AND a.spPort = #{shPort}



		 AND a.spRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
    	AND a.spRegDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
UPDATE ship
SET
	spName = #{spName}
	,spPrice = #{spPrice}
	,spDEsc = #{spDesc}
	,spTotal = #{spTotal}
	,spStart = #{spStart}
	,spArea = #{spArea}
	,spPort = #{spRegDate}
WHERE 1=1
AND spSeq = #{spSeq}
