mapper.sql:
FROM ship a

WHERE 1=1



            AND(
                a.spName LIKE CONCAT('%', #{shValue}, '%')
                OR a.spPrice LIKE CONCAT('%', #{shValue}, '%')
                OR a.spTotal LIKE CONCAT('%', #{shValue}, '%')
            )


            AND a.spName = #{shValue}


            AND a.spPrice LIKE CONCAT('%', #{shValue}, '%')


            AND a.spTotal LIKE CONCAT('%', #{shValue}, '%')




    AND a.spDelNy = #{shDelNy}


    AND a.spEatNy = #{shEatNy}


    AND a.spRentalNy = #{shRentalNy}


    AND a.spArea = #{shArea}



		 AND a.spRegDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
UPDATE ship
SET
	spName = #{spName}
	,spPrice = #{spPrice}
	,spDEsc = #{spDesc}
	,spTotal = #{spTotal}
	,spStart = #{spStart}
	,spArea = #{spArea}
	,spEatNy = #{spEatNy}
	,spRentalNy = #{spRentalNy}
	,spRegDate = #{spRegDate}
	,port_ptSeq = #{port_ptSeq}
WHERE 1=1
AND spSeq = #{spSeq}
